    "ip": "192.168.15.22",
    "deviceid": "${res.sonoff.deviceId}",
    "data": {
        "switch": "${res.payload.toLowerCase()}"
    }
}`

return msg;

const res = msg;
const data = JSON.parse(res.payload.data);

const statMessage = {
    topic: `stat/sonoffmini/${res.topic}`,
    payload: data,
}

const powerMessage = {
    topic: `stat/sonoffmini/${res.topic}/POWER`,
    payload: data.switch.toUpperCase(),
}

msg = {};

msg.payload = [statMessage, powerMessage];



return msg;

